import styled from '@emotion/styled';

const ButtonStyle = styled.button`
display = ${(props) => (props.isVisible ? '' : 'none')};

`;

const handleOffset = (offset, setOffset, maxPage) => {
  setOffset(offset + 1);
};

const handleVisible = (isVisible, isTrue) => {
  if (!isTrue) {
    isVisible = false;
  }
};

const MoreButton = (isVisible, setIsVisible, offset, setOffset, maxPage) => {
  return (
    <ButtonStyle data-testid="more-button" onClick={() => handleOffset(offset, setOffset, maxPage)}>
      테스트
    </ButtonStyle>
  );
};

export default MoreButton;
