import { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Products from './Products';
import CarDetail from './CarDetail';

import Container from '../../components/Container';
import Top from '../../components/Top';
import Top2 from '../../components/Top2';
import Carousel from '../../components/Carousel';
import AllVehicles from '../../components/AllVehicles';
import Modal from '../../components/Modal';
import MoreButton from '../../components/MoreButton';

const url = `http://localhost:8080/carClasses`;

const CarListPage = () => {
  const [items, setItems] = useState([]);
  const [offset, setOffset] = useState(1);
  const [maxPage, setMaxPage] = useState(0);
  const [isVisible, setIsVisible] = useState(true);

  /* 캐로셀 데이터 */
  const carouselRef = useRef();
  const [carouselCurrentPosition, setCarouselCurrentPosition] = useState();
  const [loop, setLoop] = useState(0);
  const [carouselSize, setCarouselSize] = useState();
  const [carouselWidth, setCarouselWidth] = useState();
  const [oneWidth, setOneWidth] = useState();

  /* 모달 */
  const [showModal, setShowModal] = useState(0);
  const [carId, setCarId] = useState(0);

  /* Dim 영역의 스크롤을 방지하기 위해 Body에 overflow 속성 변경 */
  useEffect(() => {
    const body = document.getElementsByTagName('body')[0];
    if (showModal) {
      body.style.overflow = 'hidden';
    } else {
      body.style = '';
    }
  }, [showModal]);

  /* 페이지 렌더링 시, 최초 요청 */
  useEffect(() => {
    loadItems();
  }, []);

  /* 모든 차량 조회 시, 더보기 버튼 숨기기 */
  useEffect(() => {
    if (offset === maxPage) {
      setIsVisible(false);
    }
  }, [offset, maxPage]);

  /* 캐로셀 전체 길이, 한 상품의 길이, 초기 시작 위치 설정 */
  useEffect(() => {
    setCarouselWidth(carouselRef.current.clientWidth);
    setOneWidth(carouselWidth / 2);
    setCarouselCurrentPosition(oneWidth / 2);
  }, [carouselWidth, carouselSize]);

  /* 특가 차량 무한 캐로셀 로직 : ( 절반 - 한개 - 절반 ) 
    - 1개의 길이 : 캐로셀 전체 길이 / 캐로셀 갯수
    - 시작점 : 1개의 길이 / 2;
    - 이동 거리 : 1개의 길이 만큼 translate(-${carouselCurrentPosition}px)
    - 무한 스크롤을 위해 리스트 추가 한 뒤, 슬라이더 바꿔치기 (마지막 슬라이더 -> 첫번째 슬라이더) : transition-timing-function: step-end 속성을 활용
  */

  useEffect(() => {
    if (typeof carouselCurrentPosition == 'undefined') {
      carouselRef.current.style =
        'transition-timing-function: step-end; transform : translate(-105px)';
      setCarouselCurrentPosition(oneWidth / 2);
    } else {
      carouselRef.current.style = `transform : translate(-${carouselCurrentPosition}px)`;
    }

    const caroulselLoop = setTimeout(() => {
      setCarouselCurrentPosition((prev) => {
        if (prev <= oneWidth * carouselSize) {
          return prev + oneWidth;
        } else return;
      });
    }, 3000);

    setLoop(caroulselLoop);
    return clearTimeout(loop);
  }, [carouselCurrentPosition]);

  const loadItems = async () => {
    try {
      let res = await axios.get(url);
      setItems(res.data);
      setMaxPage(Math.ceil(res.data.length / 5));
    } catch (err) {
      alert(err);
    }
  };

  return (
    <Container>
      <Top data-testid="page-title">차량 리스트</Top>

      {/* 차량 캐로셀 */}
      <Top2>특가 차량</Top2>
      <Carousel carouselRef={carouselRef}>
        <Products
          items={items}
          offset={offset}
          type="specialPriceList"
          oneWidth={oneWidth}
          setCarouselSize={setCarouselSize}
          carouselWidth={carouselWidth}
        />
      </Carousel>

      {/* 차량 리스트 */}
      <Top2>모든 차량</Top2>
      <AllVehicles>
        <Products
          items={items}
          offset={offset}
          type="allList"
          setShowModal={setShowModal}
          setCarId={setCarId}
        />
      </AllVehicles>

      {/* 차량 상세 모달 */}
      <Modal showModal={showModal} setShowModal={setShowModal}>
        <CarDetail carId={carId} showModal={showModal} />
      </Modal>

      <MoreButton isVisible={isVisible} offset={offset} setOffset={setOffset}>
        더보기
      </MoreButton>

      <button
        data-testid="more-button"
        style={{ display: isVisible ? '' : 'none' }}
        onClick={() => setOffset((offset) => offset + 1)}>
        더보기
      </button>
    </Container>
  );
};
export default CarListPage;
