import { useState, useEffect } from 'react';
import axios from 'axios';

const url = `http://localhost:8080/carClasses`;

export const CarDetail = (props) => {
  const [detailInfo, setDetailInfo] = useState([]);
  const [isPending, setIsPending] = useState(false);
  const [isSucces, setIsSucces] = useState(false);
  const [prevSelect, setPrevSelect] = useState();

  // 이전에 선택했던 항목을 다시 선택하게 되면, 변화가 없으므로 로드하지 않는다...
  // useEffect(() => {
  //   setPrevSelect(props.carId);
  //   loadDetailInfo();
  //   console.log(isPending);
  //   console.log(isSucces);
  // }, [props.carId]);

  // 모달이 닫힌 경우, 로딩과 성공 여부 초기화
  useEffect(() => {
    if (!props.showModal) {
      setIsPending(false);
      setIsSucces(false);
    } else if (props.showModal && prevSelect === props.carId) {
      setIsPending(true);
      setIsSucces(true);
    } else if (props.showModal && prevSelect != props.carId) {
      loadDetailInfo();
    }
  }, [props.showModal]);

  const loadDetailInfo = async () => {
    try {
      let res = await axios.get(`${url}/${props.carId}`);
      setDetailInfo(res.data);
      setIsPending(true);
      setIsSucces(true);
    } catch (err) {
      alert(err);
      setIsPending(true);
      setIsSucces(false);
    }
  };

  return (
    <>
      {isPending ? (
        <div>
          {detailInfo &&
            detailInfo.map((item, idx) => (
              <div key={idx}>
                <img src={item.carImage} alt={`${item.carClassName} 차량`} />
                {item.carClassName}
                <p>제조사 : {item.maker}</p>
                <p>분류 : {item.carModel}</p>
                <p>연료 : {item.fuel}</p>
                <p>변속방식 : {item.gearbox}</p>
                <p>승차정원 : {item.capacity}</p>
                안전 옵션
                <p>{item.safetyOption}</p>
                편의 옵션
                <p>{item.additionalOption}</p>
              </div>
            ))}{' '}
        </div>
      ) : (
        <div>
          <h1>Loading...</h1>
        </div>
      )}
    </>
  );
};

export default CarDetail;
