import styled from '@emotion/styled';

const PreventScroll = styled.html`
   {
    overflow: auto;
    height: 100%;
  }
`;

const ModalFrame = styled.div`
  overflow: hidden;
  position: fixed;
`;

const Dim = styled.div`
  overflow: hidden;
  position: fixed;

  justify-content: center;
  align-items: center;

  top: 0;
  left: 0;

  width: 100%;
  height: 100%;

  background-color: rgba(0, 0, 0, 0.6);
`;

const ModalBlock = styled.div`
  overflow: auto;
  position: fixed;

  z-index: 9999;
  opacity: 1;

  width: 80%;
  height: 80%;

  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  background-color: white;
`;

const Close = styled.div`
  margin: 30px 30px 0 0;
  display: flex;
  justify-content: end;
`;

/* showModal의 True/False(1/0)에 따라 모달의 표시 여부 */

const Modal = ({ showModal, setShowModal, children }) => {
  return (
    <div>
      <ModalFrame>
        <Dim style={{ display: showModal ? 'flex' : 'none' }} onClick={() => setShowModal(0)}>
          <ModalBlock>
            <Close onClick={() => setShowModal(0)}>X</Close>
            {children}
          </ModalBlock>
        </Dim>
      </ModalFrame>
    </div>
  );
};

export default Modal;
