import styled from '@emotion/styled';

const PreventScroll = styled.html`
   {
    overflow: auto;
    height: 100%;
  }
`;

const Dim = styled.div`
  overflow: hidden;
  position: fixed;

  display: ${(props) => (props.showModal ? 'none' : 'flex')};
  justify-content: center;
  align-items: center;

  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.6);
`;

// const ModalBlock = styled.div`
//   position: fixed;
//   width: 80vw;
//   height: 80vh;
//   top: 50%;
//   left: 50%;
//   opacity: 1;
//   background-color: white;
//   transform: translate(-50%, -50%);
//   z-index: 9999;
//   overflow: hidden;
// `;
const ModalBlock = styled.div`
  overflow: auto;
  width: 50%;
  height: 400px;
  padding: 2em;
  background-color: #fff;
`;

const Close = styled.div`
  margin: 30px 30px 0 0;
  display: flex;
  justify-content: end;
`;

/* showModal의 True/False(1/0)에 따라 모달의 표시 여부 */

const Modal = ({ showModal, children }) => {
  return (
    <Dim style={{ display: showModal ? 'flex' : 'none' }}>
      <ModalBlock>
        <Close>X</Close>
        {children}
      </ModalBlock>
    </Dim>
  );
};

export default Modal;
