import styled from '@emotion/styled';
import Top3 from './Top3';
import Top4 from './Top4';

const ImgContainer = styled.div`
  width: 100%;
  position: relative;
`;

const Img = styled.img`
  width: 100%;
  height: auto;

  object-fit: cover;
`;

const DescribeBlock = styled.div`
  display: grid;
  justify-items: start;
`;

const Title = styled.div`
  font-size: 20px;
  font-weight: bold;
`;

const Detail = styled.div`
  font-size: 18px;
  margin: 10px;
`;

const ModalContent = ({
  carImage,
  carClassName,
  maker,
  carModel,
  fuel,
  gearbox,
  capacity,
  safetyOption,
  additionalOption,
}) => {
  return (
    <>
      <ImgContainer>
        <Img src={carImage} alt={`${carClassName} 차량`} />
      </ImgContainer>
      <DescribeBlock>
        <Title>{carClassName}</Title>
        <Detail>제조사 : {maker}</Detail>
        <Detail>분류 : {carModel}</Detail>
        <Detail>연료 : {fuel}</Detail>
        <Detail>변속방식 : {gearbox}</Detail>
        <Detail>승차정원 : {capacity}</Detail>
        <div>안전 옵션</div>
        <div>{safetyOption}</div>
        <div>편의 옵션</div>
        <div>{additionalOption}</div>
      </DescribeBlock>
    </>
  );
};

export default ModalContent;
