import { useState, useEffect } from 'react';
import axios from 'axios';

const url = `http://localhost:8080/carClasses`;

export const CarDetail = (props) => {
  const [detailInfo, setDetailInfo] = useState([]);
  useEffect(() => {
    loadDetailInfo();
  }, [props.carId]);

  const loadDetailInfo = async () => {
    try {
      let res = await axios.get(`${url}${props.carId}`);
      setDetailInfo(res.data);
    } catch (err) {
      alert(err);
    }
  };

  return (
    <div>
      {detailInfo && detailInfo.map((item, idx) => <div key={idx}>{item.carClassName}</div>)}
    </div>
  );
};

export default CarDetail;
