import styled from '@emotion/styled';

const ImgContainer = styled.div`
  width: 100%;
  position: relative;
`;

const Img = styled.img`
  width: 100%;
  height: auto;

  object-fit: cover;
`;

const Title = styled.div`
  font-size: 20px;
  font-weight: bold;
`;

const ModalContent = ({
  carImage,
  carClassName,
  maker,
  carModel,
  fuel,
  gearbox,
  capacity,
  safetyOption,
  additionalOption,
}) => {
  return (
    <>
      <ImgContainer>
        <Img src={carImage} alt={`${carClassName} 차량`} />
      </ImgContainer>
      <Title>{carClassName}</Title>
    </>
  );
};

export default ModalContent;
