export function makeCarouselData(props, result, setInfos) {
  props.items.map((item) => {
    var findSpecialPrice = item.carTypeTags.includes('특가');
    if (findSpecialPrice) {
      result.push(item);
    }
    setInfos(result);

    props.setCarouselSize(result.length);
  });
  const a = result[0];
  const b = result[1];
  const c = result[2];
  result.push(a);
  result.push(b);
  result.push(c);

  return result;
}

export default makeCarouselData;
