import { useState, useEffect } from 'react';
import axios from 'axios';
import ModalContent from '../../components/ModalContent';

import handleModalContent from './CarDetail/handleModalContent';

const url = `http://localhost:8080/carClasses`;

export const CarDetail = (props) => {
  const [detailInfo, setDetailInfo] = useState([]);
  const [isPending, setIsPending] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [prevSelect, setPrevSelect] = useState();

  // 이전에 선택했던 항목 기록
  useEffect(() => {
    setPrevSelect(props.carId);
  }, [props.carId]);

  // 모달 내 컨텐츠 선택
  useEffect(() => {
    handleModalContent(
      props,
      setIsPending,
      prevSelect,
      loadDetailInfo,
      setDetailInfo,
      setIsSuccess,
    );
  }, [props.showModal]);

  const loadDetailInfo = async () => {
    try {
      let res = await axios.get(`${url}/${props.carId}`);
      setDetailInfo(res.data);
      setIsPending(true);
      setIsSuccess(true);
    } catch (err) {
      setIsPending(true);
      setIsSuccess(false);
    }
  };

  return (
    <>
      {isPending ? (
        isSuccess ? (
          <div>
            {detailInfo &&
              detailInfo.map((item, idx) => (
                <div key={idx}>
                  <ModalContent
                    carImage={item.carImage}
                    carClassName={item.carClassName}
                    maker={item.maker}
                    carModel={item.carModel}
                    fuel={item.fuel}
                    gearbox={item.gearbox}
                    capacity={item.capacity}
                    safetyOption={item.safetyOption}
                    additionalOption={item.additionalOption}></ModalContent>
                </div>
              ))}
          </div>
        ) : (
          <div>
            <h1>Error...</h1>
          </div>
        )
      ) : (
        <div>
          <h1>Loading...</h1>
        </div>
      )}
    </>
  );
};

export default CarDetail;
