import styled from '@emotion/styled';
import { handleRequest } from 'msw';

const ButtonStyle = styled.button`
display = ${(props) => (props.isVisible ? '' : 'none')};

`;

const handleOffset = (isVisible, offset, maxPage) => {
  offset += 1;
  if (offset === maxPage) {
    handleVisible(isVisible, false);
  }
};

const handleVisible = (isVisible, isTrue) => {
  if (!isTrue) {
    isVisible = false;
  }
};

const MoreButton = (isVisible, offset, maxPage) => {
  return (
    <ButtonStyle data-testid="more-button" onClick={() => handleOffset(isVisible, offset, maxPage)}>
      테스트
    </ButtonStyle>
  );
};

export default MoreButton;
