import Card from '../../components/Card';

export const Products = (props) => {
  const infos = props.items.slice(0, props.offset * 5);

  /* 가격 : 10자리에서 반올림 후, 3자리마다 콤마로 구분 */
  const setPrice = (olderPrice) => {
    olderPrice = Math.round(olderPrice / 10) * 10;
    let newPrice = olderPrice.toString().replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ',');
    return newPrice;
  };

  /* 거리 : 1000 이상은 천, 10000 이상은 만으로 표기 */
  const setDistance = (olderDistance) => {
    if (olderDistance >= 10000) {
      olderDistance /= 10000;

      return String(olderDistance + '만');
    } else if (olderDistance < 10000 && olderDistance >= 1000) {
      olderDistance /= 1000;

      return String(olderDistance + '천');
    }

    return olderDistance;
  };

  return (
    <>
      {infos &&
        infos.map((item) => (
          <Card.Outline key={item.carClassId}>
            <Card.Img src={item.image} alt={`${item.carClassNfame} 차량`} />

            <Card.Wrapper>
              <Card.Title>
                <p>{item.carClassName}</p>
                <p>{item.carTypeTags}</p>
              </Card.Title>

              <Card.Price>
                <p>{setPrice(item.price)}원</p>
                &nbsp;
                <p>({item.discountPercent}%)</p>
              </Card.Price>

              <Card.Etc>
                <p>{item.year}년</p>
                &nbsp; <p>{setDistance(item.drivingDistance)}km</p>
                &nbsp; <p>{item.regionGroups}</p>
              </Card.Etc>
            </Card.Wrapper>
          </Card.Outline>
        ))}
    </>
  );
};

export default Products;
