import { useEffect, useState } from 'react';
import Card from '../../components/Card';

export const Products = (props) => {
  const [infos, setInfos] = useState([]);
  const [type, setType] = useState();

  /* 공통 컴포넌트로 사용하면서 특가 차량과 모든 차량을 보여주는 것에 대한 분기 */
  useEffect(() => {
    if (props.type === 'allList') {
      setType('allList');
      setInfos(props.items.slice(0, props.offset * 5));
    } else if (props.type === 'specialPriceList') {
      setType('specialPriceList');

      const result = [];

      props.items.map((item) => {
        var findSpecialPrice = item.carTypeTags.includes('특가');
        if (findSpecialPrice) {
          result.push(item);
        }
        setInfos(result);
        return result;
      });
    }
  }, [props]);

  /* 가격 : 10자리에서 반올림 후, 3자리마다 콤마로 구분 */
  const setPrice = (olderPrice) => {
    olderPrice = Math.round(olderPrice / 10) * 10;
    let newPrice = olderPrice.toString().replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ',');
    return String(newPrice);
  };

  /* 거리 : 1000 이상은 천, 10000 이상은 만으로 표기 / 테스트를 위해 String으로 형변환 */
  const setDistance = (olderDistance) => {
    if (olderDistance >= 10000) {
      olderDistance /= 10000;

      return String(olderDistance + '만');
    } else if (olderDistance < 10000 && olderDistance >= 1000) {
      olderDistance /= 1000;

      return String(olderDistance + '천');
    }

    return String(olderDistance);
  };

  /* 지역 : '/' -> ', ' 치환 &&  ',' ->  ', '로 치환 */
  const setRegionGroups = (olderRegion) => {
    let newRegion = olderRegion.toString().replace(/\/|,/gi, ', ');
    return newRegion;
  };

  // 빠른 대여, 인기, 특가 등 종류에 따라 다른 디자인을 하기 위해 정규 표현식 대신 for문을 통해 작성
  const setTags = (olderTags) => {
    const newTags = [];
    for (let i = 0; i < olderTags.length; i++) {
      newTags.push(
        <p id={olderTags[i]} key={i}>
          {olderTags[i]}
        </p>,
      );
    }
    return newTags;
  };

  // 하단 표기 정보 길이를 찾기 위해 String 형 변환 => 차량 캐러셀이면서 길이가 20이 넘는다면 '...' 스타일 적용
  const setBottomInfomations = (year, dist, region) => {
    const newBottomInfomations = year + '년 | ' + dist + 'km | ' + region;

    if (type === 'specialPriceList' && newBottomInfomations.length > 20) {
      return <Card.OverBottomInfo>{newBottomInfomations}</Card.OverBottomInfo>;
    } else {
      return newBottomInfomations;
    }
  };

  return (
    <>
      {infos &&
        infos.map((item) => (
          <Card.Outline
            key={item.carClassId}
            style={{ width: type === 'specialPriceList' ? '60%' : '100%' }}>
            <Card.Img src={item.image} alt={`${item.carClassName} 차량`} />

            <Card.Wrapper>
              <Card.Title>
                <p>{item.carClassName}</p>
                {type === 'specialPriceList' ? '' : <h5>{setTags(item.carTypeTags)}</h5>}
              </Card.Title>

              <Card.Price>
                <p data-testid="price-text">{setPrice(item.price)}원</p>
                &nbsp;
                {item.discountPercent === 0 ? (
                  ''
                ) : (
                  <p data-testid="discountPercent-text">(-{item.discountPercent}%)</p>
                )}
              </Card.Price>

              <Card.BottomInfo>
                {setBottomInfomations(
                  item.year,
                  setDistance(item.drivingDistance),
                  setRegionGroups(item.regionGroups),
                )}
              </Card.BottomInfo>
            </Card.Wrapper>
          </Card.Outline>
        ))}
    </>
  );
};

export default Products;
