import Products from "./Products"
import { useState, useEffect } from "react";
import axios from "axios";


const url = `http://localhost:8080/carClasses`


const CarListPage = () => {

    const [items, setItems] = useState([]);
    const [offset, setOffset] = useState(1);
    const [isVisible, isSetVisible] = useState(true);
    let maxPage = 0;

    /* 페이지 렌더링 시, 최초 요청 */
    useEffect(() => {
        loadItems();
    }, []); 

    useEffect(() => {
        console.log(offset);
        if(offset === maxPage) {
            isSetVisible(false);
        }
    }, [offset, maxPage]);

    const loadItems = async () => {
        try {
            let res = await axios.get(url);
            setItems(res.data);
            maxPage = (res.data)/5;
        }
        catch(err) {
            alert(err);
        }
    }


    return (
        <div>
            <p data-testid="page-title">차량 리스트</p>
            <Products items={items} offset={offset}/>
            <button 
              style={{display: isVisible ? '' : 'none'}}
              onClick={(offset) => setOffset(offset+1)}
            >
              더보기
            </button>
        </div>
    );
};
export default CarListPage;
