import { useEffect, useState } from 'react';
import Card from '../../../components/Card';

export const Products = (props) => {
  const [infos, setInfos] = useState([]);
  const [type, setType] = useState();

  /* 공통 컴포넌트로 사용하면서 특가 차량과 모든 차량을 보여주는 것에 대한 분기 */
  useEffect(() => {
    if (props.type === 'allList') {
      setType('allList');
      setInfos(props.items.slice(0, props.offset * 5));
    } else if (props.type === 'specialPriceList') {
      setType('specialPriceList');

      const result = [];

      props.items.map((item) => {
        var findSpecialPrice = item.carTypeTags.includes('특가');
        if (findSpecialPrice) {
          result.push(item);
        }
        setInfos(result);

        props.setCarouselSize(result.length);
      });
      const a = result[0];
      const b = result[1];
      const c = result[2];
      result.push(a);
      result.push(b);
      result.push(c);

      return result;
    }
  }, [props]);

  /* 모든 차량 리스트에 대해서만, 모달에 대한 권한을 가진다. */
  const handleOpenModal = (id) => {
    if (type === 'allList') {
      props.setShowModal(1);
      props.setCarId(id);
    }
  };

  return (
    <>
      {infos &&
        infos.map((item, idx) => (
          <div
            key={idx}
            data-testid={type === 'allList' ? 'car-list-area' : ''}
            style={{ width: type === 'specialPriceList' ? `${props.oneWidth}px` : '' }}
            onClick={() => {
              handleOpenModal(item.carClassId);
            }}>
            <Card
              type={props.type}
              image={item.image}
              className={item.carClassName}
              carTypeTags={item.carTypeTags}
              price={item.price}
              discountPercent={item.discountPercent}
              year={item.year}
              drivingDistance={item.drivingDistance}
              regionGroups={item.regionGroups}></Card>
          </div>
        ))}
    </>
  );
};

export default Products;
