import styled from '@emotion/styled';

const Dim = styled.div`
  overflow: hidden;
  position: fixed;

  display: flex;
  justify-content: center;
  align-items: center;

  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.6);
`;

// const ModalBlock = styled.div`
