//   z-index: 9999;
//   overflow: hidden;
// `;
const ModalBlock = styled.div`
  overflow: auto;
  width: 50%;
  height: 400px;
  padding: 2em;
  background-color: #fff;
`;

const Close = styled.div`
  margin: 30px 30px 0 0;
  display: flex;
  justify-content: end;
`;

const Modal = ({ showModal, children }) => {
  return (
    <Dim>
      <ModalBlock>
        <Close>X</Close>
        {children}
      </ModalBlock>
    </Dim>
  );
};

export default Modal;
