import Card from '../../components/Card';

export const Products = (props) => {
  const infos = props.items.slice(0, props.offset * 5);

  /* 가격 : 10자리에서 반올림 후, 3자리마다 콤마로 구분 */
  const setPrice = (olderPrice) => {
    olderPrice = Math.round(olderPrice / 10) * 10;
    let newPrice = olderPrice.toString().replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ',');
    return newPrice;
  };

  return (
    <>
      {infos &&
        infos.map((item) => (
          <Card.Outline key={item.carClassId}>
            <Card.Img src={item.image} alt={`${item.carClassNfame} 차량`} />

            <Card.Wrapper>
              <Card.Title>
                <p>{item.carClassName}</p>
                <p>{item.carTypeTags}</p>
              </Card.Title>

              <Card.Price>
                <p>{item.price}원</p>
                <p>{setPrice(item.price)}원</p>
                <p>({item.discountPercent}%)</p>
              </Card.Price>

              <Card.Etc>
                <p>{item.year}년</p> <p>{item.drivingDistance}km</p> <p>{item.regionGroups}</p>
              </Card.Etc>
            </Card.Wrapper>
          </Card.Outline>
        ))}
    </>
  );
};

export default Products;
