import { useEffect, useState } from 'react';
import Card from '../../components/Card';
import Top2 from '../../components/Top2';

export const Carousel = (props) => {
  const allCarList = props.items;
  const [specialPrice, setSpecialPrice] = useState([]);

  useEffect(() => {
    if (allCarList.length !== 0) {
      findSpecialPrice(allCarList);
    }
  }, [allCarList]);

  const findSpecialPrice = (carList) => {
    const result = [];
    carList.map((item) => {
      var findSpecialPrice = item.carTypeTags.includes('특가');
      if (findSpecialPrice) {
        result.push(item);
      }
      setSpecialPrice(result);
    });
  };

  /* 가격 : 10자리에서 반올림 후, 3자리마다 콤마로 구분 */
  const setPrice = (olderPrice) => {
    olderPrice = Math.round(olderPrice / 10) * 10;
    let newPrice = olderPrice.toString().replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ',');
    return String(newPrice);
  };

  /* 거리 : 1000 이상은 천, 10000 이상은 만으로 표기 / 테스트를 위해 String으로 형변환 */
  const setDistance = (olderDistance) => {
    if (olderDistance >= 10000) {
      olderDistance /= 10000;

      return String(olderDistance + '만');
    } else if (olderDistance < 10000 && olderDistance >= 1000) {
      olderDistance /= 1000;

      return String(olderDistance + '천');
    }

    return String(olderDistance);
  };

  /* 지역 : '/' -> ', ' 치환 &&  ',' ->  ', '로 치환 */
  const setRegionGroups = (olderRegion) => {
    let newRegion = olderRegion.toString().replace(/\/|,/gi, ', ');
    return newRegion;
  };

  // 빠른 대여, 인기, 특가 등 종류에 따라 다른 디자인을 하기 위해 정규 표현식 대신 for문을 통해 작성
  const setTags = (olderTags) => {
    const newTags = [];
    for (let i = 0; i < olderTags.length; i++) {
      newTags.push(
        <p id={olderTags[i]} key={i}>
          {olderTags[i]}
        </p>,
      );
    }
    return newTags;
  };

  return (
    <>
      <Top2>특가 차량</Top2>
      {specialPrice &&
        specialPrice.map((item) => (
          <Card.Outline key={item.carClassId}>
            <Card.Img src={item.image} alt={`${item.carClassName} 차량`} />

            <Card.Wrapper>
              <Card.Title>
                <p>{item.carClassName}</p>
                {item.carTypeTags}
              </Card.Title>

              <Card.Price>
                <p data-testid="price-text">{item.price}원</p>
                &nbsp;
                {item.discountPercent === 0 ? (
                  ''
                ) : (
                  <p data-testid="discountPercent-text">(-{item.discountPercent}%)</p>
                )}
              </Card.Price>

              <Card.Etc>
                <p>{item.year}년 |</p>
                &nbsp;
                <p data-testid="drivingDistance-text">{item.drivingDistance}km |</p>
                &nbsp; <p>{item.regionGroups}</p>
              </Card.Etc>
            </Card.Wrapper>
          </Card.Outline>
        ))}
    </>
  );
};
