import Products from './Products';
import { useState, useEffect, useRef } from 'react';
import axios from 'axios';

import Container from '../../components/Container';
import Top from '../../components/Top';
import Top2 from '../../components/Top2';
import Carousel from '../../components/Carousel';

const url = `http://localhost:8080/carClasses`;

const CarListPage = () => {
  const [items, setItems] = useState([]);
  const [offset, setOffset] = useState(1);
  const [maxPage, setMaxPage] = useState(0);
  const [isVisible, isSetVisible] = useState(true);

  /* 캐로셀 */
  const carouselRef = useRef();
  const [carouselCurrentPosition, setCarouselCurrentPosition] = useState(105);
  const [loop, setLoop] = useState(0);
  const [carouselSize, setCarouselSize] = useState();
  const [carouselWidth, setCarouselWidth] = useState();

  /* 페이지 렌더링 시, 최초 요청 */
  useEffect(() => {
    loadItems();
  }, []);

  /* 모든 차량 조회 시, 더보기 버튼 숨기기 */
  useEffect(() => {
    if (offset === maxPage) {
      isSetVisible(false);
    }
  }, [offset, maxPage]);

  /* 캐로셀 */
  useEffect(() => {
    // console.log(carouselRef.current.clientWidth);
    setCarouselWidth(carouselRef.current.clientWidth);

    // carouselRef.current.style.transform = `translate(-${carouselCurrentPosition}vw)`;
    carouselRef.current.style.transform = `translate(-${carouselCurrentPosition}px)`;

    // 최소 0vw ~ 최대 100vw : 0 ~ 10까지를 뱉음. + 게시글 갯수, 하나의 크기
    const caroulselLoop = setTimeout(() => {
      setCarouselCurrentPosition((prev) => {
        // 움직일 거리 : 게시물 하나의 크기를 가져오면 되겠다. (전체 길이까지)
        if (prev <= 210 * (5 - 1)) {
          return prev + 210;
        } else {
          carouselRef.current.style.transform = `transition-timing-function: ease`;
          return 105;
        }
      });
    }, 3000);
    console.log(carouselCurrentPosition);

    setLoop(caroulselLoop);
    return clearTimeout(loop);
  }, [carouselCurrentPosition, carouselSize]);

  const loadItems = async () => {
    try {
      let res = await axios.get(url);
      setItems(res.data);
      setMaxPage(Math.ceil(res.data.length / 5));
    } catch (err) {
      alert(err);
    }
  };

  return (
    <Container>
      <Top data-testid="page-title">차량 리스트</Top>

      <Top2>특가 차량</Top2>
      <Carousel.NonScroll>
        <Carousel.CarouselContainer ref={carouselRef}>
          <Carousel.CarouselInner>
            <Products
              items={items}
              offset={offset}
              type="specialPriceList"
              setCarouselSize={setCarouselSize}
              carouselWidth={carouselWidth}
            />
          </Carousel.CarouselInner>
        </Carousel.CarouselContainer>
      </Carousel.NonScroll>

      <Top2>모든 차량</Top2>
      <div>
        <Products items={items} offset={offset} type="allList" />
      </div>

      <button
        data-testid="more-button"
        style={{ display: isVisible ? '' : 'none' }}
        onClick={() => setOffset((offset) => offset + 1)}>
        더보기
      </button>
    </Container>
  );
};
export default CarListPage;
